con:config.script:
import java.nio.*
import sun.misc.BASE64Decoder

def groovyUtils = new com.eviware.soapui.support.GroovyUtils(context)
def response = context.expand('${getInstalledEdls#Response}');
def responseParser = new XmlParser().parseText(response)
def soap_ns = new groovy.xml.Namespace("http://schemas.xmlsoap.org/soap/envelope/", 'SOAP-ENV')
def edlprovider_ns = new groovy.xml.Namespace("http://temp/edlprovider", 'edlprovider')

responseParser[soap_ns.Body][edlprovider_ns.installedEdlsResponse][edlprovider_ns.Map].item.each { Item ->	
	edlType = Item.edlType.text()
	edlExtension = Item.edlExtension.text()
	log.info(edlType)
	log.info(edlExtension)

	testRunner.testCase.properties['CurrentFormat'].setValue(edlType)

	testRunner.testCase.testSteps['getEdlDouble'].run(testRunner, context)

 	log.info(groovyUtils.projectPath)

	def edlRespHolder = groovyUtils.getXmlHolder("getEdlDouble#Response")
	edlRespResult = edlRespHolder.getNodeValue("//*[local-name()='getEdlResponse']/*[local-name()='getEdlResult']/text()")
   	byte[] nodeDecoded = new BASE64Decoder().decodeBuffer(edlRespResult)

   	assert nodeDecoded != null   	

	if (new File(groovyUtils.projectPath, 'EDL_Plugins').exists())
	{		
		edlFile = new File(groovyUtils.projectPath, 'EDL_Plugins' + File.separator + edlType + edlExtension)

   		//First delete files in case they were already created
   		if (edlFile.exists())
   			edlFile.delete()

		edlFile.withOutputStream { stream ->
			stream.write(nodeDecoded)
		} 	
   	}
}
